import React, { useState, useEffect } from "react";
import Card from "./CustomCard";
import Grid from "@material-ui/core/Grid";

import useAxios from "axios-hooks";
const MainPage = () => {
  // const [data, setData] = useState("");
  // axios
  //   .get(`https://api.punkapi.com/v2/beers?page=1&per_page=80`)
  //   .then((results) => setData(results));
  const [{ data, loading, error }, refetch] = useAxios(
    "https://nl-static-site-assets.s3.ap-south-1.amazonaws.com/reports.json"
  );

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error!</p>;

  return (
    console.log(data.items[0].story["hero-image-s3-key"]),
    (
      <div>
        <Grid
          container
          spacing={4}
          direction="row"
          justifyContent="space-evenly"
          alignItems="baseline"
        >
          {data.items.map((item, key) => (
            <Grid item xs={12} sm={6} md={3}>
              <Card
                descp={item.item.headline[0]}
                image={item.story["hero-image-s3-key"]}
              />
            </Grid>
          ))}
        </Grid>
      </div>
    )
  );
};
export default MainPage;
