import React, { useState, useEffect } from "react";
import Card from "./CustomCard";
import useAxios from "axios-hooks";
const MainPage = () => {
  // const [data, setData] = useState("");
  // axios
  //   .get(`https://api.punkapi.com/v2/beers?page=1&per_page=80`)
  //   .then((results) => setData(results));
  const [{ data, loading, error }, refetch] = useAxios(
    "https://nl-static-site-assets.s3.ap-south-1.amazonaws.com/reports.json"
  );

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error!</p>;

  return (
    console.log(data.items[0].item.headline[0]),
    (
      <div>
        {/* <button onClick={refetch}>refetch</button>
      <pre>{JSON.stringify(data, null, 2)}</pre> */}

        {data.map((item, key) => (
          <Card descp={item.items[key].item.headline[key]} />
        ))}
      </div>
    )
  );
};
export default MainPage;
