import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import FavoriteIcon from "@material-ui/icons/Favorite";

const useStyles = makeStyles(() => ({
  root: {
    maxWidth: 350,
    maxHeight: 500,
  },
  media: {
    height: 0,
    paddingTop: "56.25%",
  },
}));

export default function CustomCard({ descp, image, id }) {
  const classes = useStyles();
  const img = `https://gumlet.assettype.com/${image}`;
  const [color, setColor] = React.useState("");

  const favoriteItem = () => {
    if (color === "red") {
      setColor("");
      localStorage.removeItem(id);
    } else {
      setColor("red");
      localStorage.setItem(id, true);
    }
  };
  React.useEffect(() => {
    if (localStorage.getItem(id) !== null) {
      setColor("red");
    }
  }, []);

  return (
    <Card className={classes.root}>
      <CardMedia className={classes.media} image={img} title="Paella dish" />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {descp}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon style={{ color: color }} onClick={favoriteItem} />
        </IconButton>
      </CardActions>
    </Card>
  );
}
